ifMode.script:
jq(function() {

    // return appointment date can never be before encounter date
    var setMinReturnVisitDate = function () {

        var returnVisitDate = getValue('return-visit-date.value');

        getField('return-visit-date.value').datepicker('option', 'minDate', getField('encounterDate.value').datepicker('getDate'));

        if (returnVisitDate  && returnVisitDate != getValue('return-visit-date.value')) {
            setValue('return-visit-date.value','');  // reset return visit date if change to encounter date makes it invalid
            alert('');
        }
    }

    getField('encounterDate.value').change(setMinReturnVisitDate);

    setMinReturnVisitDate();

    jq('.medication-checkbox > input:checkbox').each(function(idx, it){
        if(jq(it).not(':checked')){
            jq(it).closest("td").siblings().find(":input").each(function(i, el){
                jq(el).attr("disabled", "disabled");
            });
        }

        jq(it).change(function(){
            if(jq(this).is(':checked')){
                jq(this).closest("td").siblings().find(":input").each(function(i, el){
                    jq(el).removeAttr("disabled");
                });
            } else {
                jq(this).closest("td").siblings().find(":input").each(function(i, el){
                    jq(el).val('').attr("disabled", "disabled");
                });
            }
        });
    });

    jq('#phq9Score > input:text').attr('placeholder', '0-27');

});

jq(function() {

var validateFamHistoryText = function(divElem) {
var parentDiv = divElem.closest('.medicalHistoryText');

if (typeof parentDiv !== "undefined" ) {
var fieldError = parentDiv.find('.field-error');
var commentsInput= parentDiv.find('input[type=text]');
if (typeof commentsInput !== "undefined" ) {
var comments = commentsInput.val();
if ((typeof comments !=="undefined") && comments.length > 0 ) {
if ( parentDiv.find('input:checked').length < 1 ) {
if (parentDiv.hasClass("famHistory")) {
fieldError.html("").show();
} else if (parentDiv.hasClass("patientHistory")) {
fieldError.html("").show();
}

return false;
}
} else {
// if there are no comments make sure the adjacent Parent checkbox is not checked
if ( parentDiv.find('input:checked').length > 0 ) {
fieldError.html("").show();
return false;
}
}
}
fieldError.html(" ").hide();
}
return true;
}


jq('.medicalHistoryText').find('input[type=checkbox]').change(function() {
validateFamHistoryText(jq(this));
});

jq('.medicalHistoryText').find('input[type=text]').change(function() {
validateFamHistoryText(jq(this));
});

beforeSubmit.push(function() {
var numErrors = 0;
jq('.medicalHistoryText').find('input[type=text]').each(function() {
if (!validateFamHistoryText(jq(this)) ) {
++numErrors;
}
});
return ( numErrors == 0 );
});

});
